    border: 2px solid $accent-color;
    border-radius: 25px;
    
}

.btn:hover {
    
    background: rgb(206,225,234);
    background: linear-gradient(0deg, rgba(206,225,234,1) 0%, rgba(34,193,195,1) 100%);
}

.text-highlight {
    color: $primary-color;
}

section {
    padding: 2rem;
    min-height: 100vh;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
}

.fifty {
    height: 50vh;
}


//Navigation Header
header {
    top: 0;
    left: 0;
    width: 100%;
    background-color: black;

    @include mediaMd  {
        position: fixed;
    }
    @include mediaSm  {
        position: fixed;
    }
    
}

nav {
    padding: 1rem;
    display: flex;
    flex-direction: row;
    height: 10vh;

    .menu-btn {
        position: absolute;
        position: fixed;
        z-index: 3;
        right: 4rem;
        top: 2rem;
        cursor: pointer;
        visibility: hidden;
        
    
        .btn-line { 
            width: 28px;
            height: 3px;
            margin: 0 0 5px 0;
            background: $primary-color;
            @include easeOut;
        }

        //MENU LINES ROTATE INTO X
        &.close {
            
            transform: rotate(180deg);

            .btn-line {
                //LINE 1 - ROTATE
                &:nth-child(1) {
                    transform: rotate(45deg) translate(5px, 5px);
                }
                //LINE 2 - HIDE
                &:nth-child(2) {
                    opacity: 0;
                }
                //LINE 3 - ROTATE
                &:nth-child(3) {
                    transform: rotate(-45deg) translate(7px, -6px);
                }
            }
        }

        @include mediaXl {
            display: none;
        }

        @include mediaLg {
            display: none
        }

        @include mediaMd {
            visibility: visible;
        }
        @include mediaSm {
            visibility: visible;
        }
    }

    .logo {
        width: 10rem;
    }

    .nav-list {
        display: flex;
        flex-direction: row;
        align-items: center;


        a {
            font-size: 1.5rem;
            color: $primary-color;
            @include easeOut;
        }
        a:hover {
            color: $accent-color;
        }

        li {
            margin: 0 3rem;
            line-height: 8rem;
            position: relative;

            .sub-menu {
                width: 20rem;
                display: block;
                position: absolute;
                border-top: 3px solid $primary-color;
                background-color: #191919;
                z-index: 100;
                top: 16rem;
                opacity: 0;
                visibility: hidden;
                @include easeOut;

                li {
                    line-height: 4rem;
                }
            }
        }

        li:hover {
            .sub-menu {
                top: 10vh;
                opacity: 1;
                visibility: visible;
            }
        }

        // Media Queries
        @include mediaMd {
            top: 0;
            left: 0;
            position: fixed;
            // height: 100vh;
            width: 100%;
            flex-direction: column;
            background-color: #191919;
            position: absolute;
            transform: translateY(-120%);
            @include easeOut;

            li {
                line-height: 4rem;
                overflow: hidden;
                .sub-menu {
                    opacity: 0;
                    transform: translateY(-120%);
                    visibility: hidden;
                    // background-color: $accent-color;
                }

            }

            li:hover {
                .sub-menu {
                    margin-left: 1rem;
                    position: initial;
                    // transition: all .2s ease-in;
                    transform: translateY(0);
                }
            }

            //show class added on menu icon click
            &.show {
                transform: translateY(0);
            }
        }
        @include mediaSm {
            top: 0;
            left: 0;
            position: fixed;
            // height: 100vh;
            width: 100%;
            flex-direction: column;
            background-color: #191919;
            position: absolute;
            transform: translateY(-120%);
            @include easeOut;

            li {
                line-height: 4rem;
                overflow: hidden;
                .sub-menu {
                    opacity: 0;
                    transform: translateY(-120%);
                    visibility: hidden;
                    // background-color: $accent-color;
